<script>
    // Internal
    import Logo from '~/components/Logo.vue'
    import Intro from '~/components/Intro.vue'
    import Cursus from '~/components/Cursus.vue'
    import Skills from '~/components/Skills.vue'
    import Projects from '~/components/Projects.vue'
