    export default {
        components: {
            // Internal
            Logo,
            Intro,
            Cursus,
            Skills,
            Projects,
            Extra,
            Contact,
            Credits
        },
        data: () => {
            let visits = Number(localStorage.getItem('visitCount'));
            localStorage.setItem('visitCount', visits+1);
            return {
                theme: `theme-` + (visits - 2)%31
            }
        }
    }
</script>

